    public void printName(String name,int count1,int count2){
        SearchChar searchChar = new SearchChar();
        String string1 = searchChar.removeMultiPinyin(name);

        //名字的第二字和第三字笔画数分别为9+23，10+7，9+7，22+15，2+14，20+4，8+24，3+14
        String string2 = searchChar.searchStrokeCount(string1,count1);
        String string3 = searchChar.searchStrokeCount(string1,count2);
        //System.out.println(string3);

        List<String> stringList = searchChar.composeName(string2,string3);

        for(String item:stringList){
            System.out.println(item);
        }
    }

